// AccountDemo.java provides a simple test bed for the AccountKind package.//// Started by: Charles Hoot, for Hands On Java.// Date: April 3, 2018      // Finished by: Adam Abad// Purpose: To learn how to use extended classes, and abstract classes//// **************************************************************import java.util.*;  // Scannerimport java.io.*;    // PrintStreamimport AccountKinds.*;class AccountDemo extends Object{  static PrintStream theScreen = new PrintStream(System.out);  static Scanner theKeyboard = new Scanner(System.in);	public static void main(String args[])	{		theScreen.println("\nWelcome to the Account package demonstrator!\n");		theScreen.print("\nCreating a Regular Account: ");		RegularAccount anAccount;		anAccount = new RegularAccount("The green hornet", "1202", 50.0);						theScreen.println("\nAccount status is: " + anAccount);				// Add test lines for Experiments here		theScreen.println("The account is owned by " + anAccount.getName());		theScreen.println("Depositing 100.00 ");		anAccount.deposit(100.00,"1202");		theScreen.println("\nAccount status is: " + anAccount);		theScreen.println("Withdrawing 90.00 ");		anAccount.withdraw(90.00,"1202");		theScreen.println("\nAccount status is: " + anAccount);		theScreen.print("\nCreating a Checking Account: ");		CheckingAccount anotherAccount;		anotherAccount = new CheckingAccount("The lone ranger", "9999", 200.0);   		theScreen.println("\nAccount status is: " + anotherAccount);				theScreen.print("\nDoing a monthly update: ");		anotherAccount.monthly_update();		theScreen.println("\nAccount status is: " + anotherAccount);		theScreen.println("Depositing 10.00 ");		anotherAccount.deposit(10.00, "9999");		theScreen.println("\nAccount status is: " + anotherAccount);		theScreen.println("Depositing 20.00 ");		anotherAccount.deposit(20.00, "9999");		theScreen.println("\nAccount status is: " + anotherAccount);		theScreen.println("Withdrawing 10.00 ");		anotherAccount.withdraw(10.0, "9999");		theScreen.println("\nAccount status is: " + anotherAccount);		theScreen.print("\nDoing a second monthly update: ");		anotherAccount.monthly_update();		theScreen.println("\nAccount status is: " + anotherAccount);	}} // end of class