//Calculate2.java provides a simple 5-function calculator.//// Adapted by: Charles Hoot, for Hands On Java.// From: Joel Adams, for Hands On C++.// Completed by: Adam Abad// Date: March 13, 2018// Purpose: To use boolean expressions and loops to make a//          useable calculator. //// Specification:// input(keyboard): a char, stored in operator;//                  two reals, stored in op1 and op2;// output(screen): the result of the expression (op1 operator op2).// **************************************************************import java.util.*;  // Scannerimport java.io.*;    // PrintStreamclass Calculate2 extends Object{	public static void main(String args[])	{		PrintStream theScreen = new PrintStream(System.out);		Scanner theKeyboard = new Scanner(System.in);		final String MENU = "\nPlease enter:\n" +                      	"\ta, to perform addition;\n" +	              		"\tb, to perform subtraction;\n" +                      	"\tc, to perform multiplication;\n" +                      	"\td, to perform division;\n" +                      	"\te, to perform exponentiation;\n" +		        "\tf, to exit the program;\n" +                      	"--> ";                      			double op1 = 0.0, op2 = 0.0;		theScreen.println("\nWelcome to the 5-function calculator!\n");		while(true)		{		char operation = getMenuChoice(MENU, theKeyboard, 'a', 'f');		if(operation == 'f') break;		while(true)		{		theScreen.print("\nNow enter your operands: ");		try		{		op1 = theKeyboard.nextDouble();		op2 = theKeyboard.nextDouble();		String junk = theKeyboard.nextLine();		break;		}		catch (InputMismatchException ex)		{		    System.out.println("\nERROR: entered a non-number!");		    System.out.println("\nTry Again!");		    String junk = theKeyboard.nextLine();	        }		} //End of inner while		if (operation == 'd' && op2 == 0.0)		   {				theScreen.println("ERROR: division by 0.");				System.exit(1);		   		   }		double result = apply(operation, op1, op2);		theScreen.println("\nThe result is " + result);		} //end while	}	    private static char getMenuChoice(String MENU, Scanner theKeyboard,				      char first, char last)	{	    char choice;	    do		{		System.out.print(MENU);		choice = theKeyboard.nextLine().charAt(0);		} while((choice < first) || (choice > last));	    		return choice;	}	private static double apply(char operation, double op1, double op2)	{		switch (operation)		{			case 'a':				return op1 + op2;			case 'b':				return op1 - op2;			case 'c':				return op1 * op2;			case 'd':				return op1 / op2;			case 'e':				return power(op1, (int) op2);			default:				System.out.println("\nInvalid operation "					+ operation					+ " received by calculator!\n");		}	return 0.0;  }    public static double power(double op1, int op2)    {	double value = 1;	for(int x = 0; x < op2; x++)	    value = value * op1;	return value;    }} // end of class